-- PARTE 1
CREATE TABLESPACE mid_term DATAFILE 
'df_mid_term.dbf' SIZE 50M;

CREATE USER rbrand
IDENTIFIED BY rbrand
DEFAULT TABLESPACE mid_term
QUOTA UNLIMITED ON mid_term;

CREATE ROLE MINI_DBA;
GRANT CREATE SEQUENCE, CREATE TABLE TO MINI_DBA;

CREATE ROLE STUDENT;
GRANT connect, MINI_DBA TO STUDENT;

GRANT STUDENT TO rbrand;

-- PARTE 2
CREATE SEQUENCE MUNICIPIOS_SEQ MAXVALUE 100 INCREMENT BY 1 START WITH 10;
CREATE SEQUENCE CAPTURAS_SEQ MAXVALUE 6000 INCREMENT BY 1 START WITH 5000;

CREATE TABLE MUNICIPIOS
( ID INTEGER, 
NOMBRE VARCHAR(256),
CONSTRAINT municipios_pk PRIMARY KEY (ID)
);


CREATE TABLE CAPTURAS 
(ID INTEGER, 
FECHA DATE, 
DEPARTAMENTO VARCHAR(255), 
MUNICIPIO_ID INTEGER, 
DIA VARCHAR(255), 
BARRIO VARCHAR(255), 
ZONA VARCHAR(255), 
CLASE_SITIO VARCHAR(255), 
EDAD INTEGER, 
SEXO VARCHAR(255), 
ESTADO_CIVIL VARCHAR(255),
CLASE_EMPLEADO VARCHAR(255), 
PROFESION VARCHAR(255), 
ESCOLARIDAD VARCHAR(255), 
DELITO VARCHAR(255),
CONSTRAINT capturas_pk PRIMARY KEY (ID),
CONSTRAINT fk_municipio_id FOREIGN KEY (MUNICIPIO_ID) REFERENCES municipios(ID)
);


REM INSERTING into RBRAND.CAPTURAS
SET DEFINE OFF;
